var http = require("http"),
    socketIo = require("socket.io");

class ChatServer {
    constructor(vamtiger) {
        this.vamtiger = vamtiger ? vamtiger : null;

        this.http = this._http;
        this.socket = this._socket;
    }

    get _http() {
        return http.createServer(this.vamtiger.handle.httpRequest);
    }

    get _socket() {
        return socketIo(this.http);
    }
}

module.exports = ChatServer;
